-- Table structure for cz_admin_user
-- ----------------------------
CREATE TABLE `cz_admin_user` (
  `uid` tinyint(3) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(10) NOT NULL,
  `password` char(32) NOT NULL,
