-- ----------------------------
-- Table structure for cz_dongyao
-- ----------------------------
CREATE TABLE `cz_dongyao` (
  `id` tinyint(4) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(4) NOT NULL COMMENT '动爻名称',
  `position` tinyint(1) unsigned NOT NULL COMMENT '动爻所在位置',
  `yaoci` varchar(400) NOT NULL COMMENT '动爻爻辞（梅花易数）',
  `s_gua_id` tinyint(3) unsigned NOT NULL COMMENT '所在原卦id',
  `b_gua_id` tinyint(3) unsigned NOT NULL COMMENT '所变之卦id',
  `tiyong` varchar(30) NOT NULL COMMENT '体用关系',
  `wuxing` varchar(30) DEFAULT NULL COMMENT '五行关系',
  `shaoci` varchar(100) DEFAULT NULL COMMENT '邵康节解卦',
  PRIMARY KEY (`id`),
  UNIQUE KEY `gua_id` (`s_gua_id`,`position`)
) ENGINE=InnoDB AUTO_INCREMENT=116 DEFAULT CHARSET=utf8;

