-- Table structure for cz_e_gua
-- ----------------------------
CREATE TABLE `cz_e_gua` (
  `id` tinyint(1) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(10) NOT NULL COMMENT '卦名',
  `thumb` varchar(100) NOT NULL COMMENT '卦象',
  `attribute` char(2) NOT NULL COMMENT '五行属性',
  `direction` char(4) NOT NULL COMMENT '地理方位',
