-- Table structure for cz_logs
-- ----------------------------
CREATE TABLE `cz_logs` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `fid` int(8) unsigned NOT NULL COMMENT '表单id',
  `dongyao_id` tinyint(4) DEFAULT NULL COMMENT '动爻Id',
  `ctime` int(10) unsigned DEFAULT NULL,
  `user_feed` tinyint(1) unsigned NOT NULL COMMENT '用户反馈:0 未表态，1准，2不准',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for cz_s_gua
-- ----------------------------
CREATE TABLE `cz_s_gua` (
  `id` tinyint(2) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(4) NOT NULL COMMENT '卦名',
  `fullname` varchar(20) NOT NULL COMMENT '卦全名',
  `thumb` varchar(100) NOT NULL COMMENT '卦象',
  `up_id` tinyint(1) unsigned NOT NULL COMMENT '上卦id',
  `down_id` tinyint(1) unsigned NOT NULL COMMENT '下卦id',
