-- ----------------------------
-- Table structure for cz_user_post
-- ----------------------------
CREATE TABLE `cz_user_post` (
  `fid` int(8) NOT NULL AUTO_INCREMENT,
  `truename` varchar(30) NOT NULL COMMENT '测字者姓名',
  `birthday` int(11) DEFAULT NULL COMMENT '生辰',
  `problem_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '问题类型，0其他，1 事业，2爱情，3 问事 ',
  `problem_text` varchar(100) NOT NULL COMMENT '问题详述',
  `words` char(4) NOT NULL COMMENT '所测字',
  `ctime` int(11) NOT NULL COMMENT '提交时间',
  `t1_len` tinyint(2) unsigned NOT NULL COMMENT '第一个字笔画',
  `t2_len` tinyint(2) unsigned NOT NULL COMMENT '第二个字笔画',
  `up_gua` tinyint(1) unsigned NOT NULL COMMENT '上卦数',
  `down_gua` tinyint(1) unsigned NOT NULL COMMENT '下卦数',
  `dongyao` tinyint(1) unsigned NOT NULL COMMENT '动爻数',
  PRIMARY KEY (`fid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

