INSERT INTO `cz_result` VALUES (1,10,24,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:2:\"24\";s:4:\"name\";s:6:\"无妄\";s:8:\"fullname\";s:12:\"天雷无妄\";s:5:\"thumb\";s:20:\"/data/thumb/s_24.png\";s:5:\"up_id\";s:1:\"1\";s:7:\"down_id\";s:1:\"4\";s:11:\"description\";s:165:\"（卦形：乾上震下）此卦‘伟大、亨通、祥和、坚贞’四德具备。只要坚持正义就可以无往不胜。如果走向邪道则必然不利。\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:2:\"20\";s:4:\"name\";s:6:\"噬嗑\";s:8:\"fullname\";s:12:\"火雷噬嗑\";s:5:\"thumb\";s:20:\"/data/thumb/s_20.png\";s:5:\"up_id\";s:1:\"3\";s:7:\"down_id\";s:1:\"4\";s:11:\"description\";s:108:\"（卦形：离上震下）噬嗑就是咀；咀嚼要破碎食物，表示要铲除障碍，才能亨通。\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:2:\"24\";s:4:\"name\";s:6:\"九五\";s:8:\"position\";s:1:\"5\";s:5:\"yaoci\";s:39:\"九五。无妄之疾，勿药有喜。\";s:7:\"xiangci\";s:39:\"象曰：无妄之药，不可试也。\";s:8:\"s_gua_id\";s:2:\"24\";s:8:\"b_gua_id\";s:2:\"20\";s:6:\"tiyong\";s:0:\"\";s:12:\"yaoci_jieshi\";s:63:\"九五：患意外之病，不要忙乱服药，自可痊愈。\";s:14:\"xiangci_jieshi\";s:63:\"《象辞》说：出人意外的药物，不可随便服用。\";s:6:\"shaoci\";s:66:\"喜喜喜，春风生桃李。不用强忧煎，明月人千里。\";}s:9:\"user_data\";a:7:{s:8:\"truename\";s:9:\"罗奂军\";s:7:\"shichen\";s:3:\"巳\";s:8:\"birthday\";s:17:\"2013年01月09日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 10:59:02\";s:4:\"word\";s:7:\"为 胡\";s:12:\"problem_text\";s:18:\"撒旦发送的发\";s:5:\"ce_sn\";s:11:\"00030050001\";}}',1382497142,0),(2,11,NULL,'a:4:{s:5:\"s_gua\";N;s:5:\"b_gua\";b:0;s:7:\"dongyao\";b:0;s:9:\"user_data\";a:8:{s:8:\"truename\";s:9:\"罗奂军\";s:7:\"shichen\";s:3:\"午\";s:8:\"birthday\";s:17:\"2013年10月21日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 11:57:32\";s:4:\"word\";s:7:\"罗 非\";s:12:\"problem_text\";s:18:\"菲菲啊菲菲额\";s:12:\"problem_type\";s:6:\"爱情\";s:5:\"ce_sn\";s:11:\"00040080001\";}}',1382500652,0),(3,12,127,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:1:\"2\";s:4:\"name\";s:3:\"坤\";s:8:\"fullname\";s:9:\"坤为地\";s:5:\"thumb\";s:19:\"/data/thumb/s_2.png\";s:5:\"up_id\";s:1:\"8\";s:7:\"down_id\";s:1:\"8\";s:11:\"description\";s:300:\"（卦形：坤上坤下）此卦象征大地，大地可以包容万物，好像君子宽阔的胸怀；大地依顺着天资生万物，向前永远奔腾不息。但大地包容既为应当收敛，此时就应该谨行慎言，才不会发生过失，即使不能得到赞赏，也可免除灾祸\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:1:\"7\";s:4:\"name\";s:3:\"比\";s:8:\"fullname\";s:9:\"水地比\";s:5:\"thumb\";s:19:\"/data/thumb/s_7.png\";s:5:\"up_id\";s:1:\"6\";s:7:\"down_id\";s:1:\"8\";s:11:\"description\";s:174:\"（卦形：坎上坤下）此卦为比卦；象征大家相亲相爱，和睦吉祥。如果看到别人团结和睦，心里不安才过去团结的人，必然有凶险。\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:3:\"386\";s:4:\"name\";s:6:\"六五\";s:8:\"position\";s:1:\"5\";s:5:\"yaoci\";s:27:\"六五。黄裳，元吉。\";s:7:\"xiangci\";s:41:\"  象曰：黄裳元吉，文在中也。\";s:8:\"s_gua_id\";s:1:\"2\";s:8:\"b_gua_id\";s:1:\"7\";s:6:\"tiyong\";s:0:\"\";s:12:\"yaoci_jieshi\";s:42:\"六五：黄色的裙裤，大吉大利。\";s:14:\"xiangci_jieshi\";s:96:\"《象辞》说：黄色的裙裤大吉大利，因为“黄裳”象征着人内在的美德。\";s:6:\"shaoci\";s:147:\"吉：得此爻者，将会获得财利，事事安稳，没有灾难。做官的将会升迁，飞黄腾达。女命则为德妇，贤惠之人。\";}s:9:\"user_data\";a:8:{s:8:\"truename\";s:9:\"罗奂军\";s:7:\"shichen\";s:3:\"午\";s:8:\"birthday\";s:17:\"2013年10月21日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 11:59:38\";s:4:\"word\";s:7:\"罗 非\";s:12:\"problem_text\";s:18:\"菲菲啊菲菲额\";s:12:\"problem_type\";s:6:\"爱情\";s:5:\"ce_sn\";s:11:\"00060000001\";}}',1382500778,0),(4,13,3,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:1:\"1\";s:4:\"name\";s:3:\"乾\";s:8:\"fullname\";s:9:\"乾为天\";s:5:\"thumb\";s:19:\"/data/thumb/s_1.png\";s:5:\"up_id\";s:1:\"1\";s:7:\"down_id\";s:1:\"1\";s:11:\"description\";s:207:\"（卦形：乾上乾下）此卦的本意是指日出时，光气舒展的形象，也有发音近似的“健”的含意。乾卦为天的极至，象征事情要进行到底，一往无前肯定可以成\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:1:\"9\";s:4:\"name\";s:3:\"履\";s:8:\"fullname\";s:9:\"天泽履\";s:5:\"thumb\";s:19:\"/data/thumb/s_9.png\";s:5:\"up_id\";s:1:\"1\";s:7:\"down_id\";s:1:\"2\";s:11:\"description\";s:144:\"（卦形：乾上兑下）此卦的卦辞为：履虎尾，不咥人，亨。表示踩了老虎尾巴，却没有受到伤害，可以亨通。\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:1:\"3\";s:4:\"name\";s:6:\"九三\";s:8:\"position\";s:1:\"3\";s:5:\"yaoci\";s:57:\"九三。君子终日乾乾，夕惕若；厉，无咎。\";s:7:\"xiangci\";s:39:\"象曰：终日乾乾，反复道也。\";s:8:\"s_gua_id\";s:1:\"1\";s:8:\"b_gua_id\";s:1:\"9\";s:6:\"tiyong\";s:72:\"体卦旺相，主卦用比和，变卦体用比和，事情能成功。\";s:12:\"yaoci_jieshi\";s:123:\"九三：有才德的君子始终是白天勤奋努力，夜晚戒惧反省，虽然处境艰难，但终究没有灾难。\";s:14:\"xiangci_jieshi\";s:87:\"《象辞》说：君子整日里勤奋努力，意思是反复行道，坚持不舍。\";s:6:\"shaoci\";s:249:\"平：得此爻者，会往来不停，财利难获，凡事不认真计划，躁动者会有损失。做官的会身兼重职，且事多繁冗，如果能保持忧虑和谨慎的态度则没有过失。女命逢之，性躁，难成为贤内助。\";}s:9:\"user_data\";a:8:{s:8:\"truename\";s:9:\"罗奂军\";s:7:\"shichen\";s:3:\"午\";s:8:\"birthday\";s:17:\"2009年09月29日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 12:36:15\";s:4:\"word\";s:7:\"测 是\";s:12:\"problem_text\";s:18:\"撒旦发送地方\";s:12:\"problem_type\";s:6:\"杂事\";s:5:\"ce_sn\";s:11:\"00070020001\";}}',1382502975,0),(5,14,127,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:2:\"14\";s:4:\"name\";s:3:\"谦\";s:8:\"fullname\";s:9:\"地山谦\";s:5:\"thumb\";s:20:\"/data/thumb/s_14.png\";s:5:\"up_id\";s:1:\"8\";s:7:\"down_id\";s:1:\"7\";s:11:\"description\";s:132:\"（卦形：坤上艮下）谦就是谦逊；谦虚就可以亨通，即使开始困难，最终也会受到赏识，得以成功。\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:2:\"52\";s:4:\"name\";s:3:\"艮\";s:8:\"fullname\";s:9:\"艮为山\";s:5:\"thumb\";s:20:\"/data/thumb/s_52.png\";s:5:\"up_id\";s:1:\"7\";s:7:\"down_id\";s:1:\"7\";s:11:\"description\";s:138:\"（卦形：艮上艮下）艮的卦象是二山重叠，稳重、静止。人们应效法这一精神，当止则止不可超越一步。\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:3:\"381\";s:4:\"name\";s:6:\"上六\";s:8:\"position\";s:1:\"6\";s:5:\"yaoci\";s:45:\"上六。鸣谦，利用行师，征邑国。\";s:7:\"xiangci\";s:63:\"象曰：鸣谦，志未得也；可用行师，征邑国也。\";s:8:\"s_gua_id\";s:2:\"14\";s:8:\"b_gua_id\";s:2:\"52\";s:6:\"tiyong\";s:0:\"\";s:12:\"yaoci_jieshi\";s:60:\"上六：明智而谦让，出兵征伐邑国自然获胜。\";s:14:\"xiangci_jieshi\";s:102:\"《象辞》说：明智而谦让，尚不能感化邑国得行其志，就可以出兵征讨邑国。\";s:6:\"shaoci\";s:102:\"平：得此爻者，有争诉之扰，但会不辨自明。做官的须修德养身，进取有望。\";}s:9:\"user_data\";a:8:{s:8:\"truename\";s:9:\"李仲举\";s:7:\"shichen\";s:3:\"申\";s:8:\"birthday\";s:17:\"1987年10月07日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 16:40:32\";s:4:\"word\";s:7:\"李 罗\";s:12:\"problem_text\";s:6:\"爱情\";s:12:\"problem_type\";s:6:\"爱情\";s:5:\"ce_sn\";s:11:\"00080050001\";}}',1382517632,0),(6,15,127,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:2:\"28\";s:4:\"name\";s:3:\"坎\";s:8:\"fullname\";s:9:\"坎为水\";s:5:\"thumb\";s:20:\"/data/thumb/s_28.png\";s:5:\"up_id\";s:1:\"6\";s:7:\"down_id\";s:1:\"6\";s:11:\"description\";s:117:\"（卦形：坎上坎下）人生遇到坎坷，失落之中不可改变自己的信念，才能得到好的结果。\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:2:\"48\";s:4:\"name\";s:3:\"井\";s:8:\"fullname\";s:9:\"水风井\";s:5:\"thumb\";s:20:\"/data/thumb/s_48.png\";s:5:\"up_id\";s:1:\"6\";s:7:\"down_id\";s:1:\"5\";s:11:\"description\";s:177:\"（卦形：坎上巽下）井是人们生活的必须品，井水永远保持干净，象征君子的美德。但打水的水桶可能倾覆，所以平静中暗藏着凶险。\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:3:\"276\";s:4:\"name\";s:6:\"六三\";s:8:\"position\";s:1:\"3\";s:5:\"yaoci\";s:60:\"六三。来之坎坎，险且枕，入于坎窞，勿用。\";s:7:\"xiangci\";s:39:\"象曰：来之坎坎，终无功也。\";s:8:\"s_gua_id\";s:2:\"28\";s:8:\"b_gua_id\";s:2:\"48\";s:6:\"tiyong\";s:0:\"\";s:12:\"yaoci_jieshi\";s:87:\"六三：向坑坎走来，这坑坎既险又深，陷入重坎之中，非常不利。\";s:14:\"xiangci_jieshi\";s:57:\"《象辞》说：来到这多坎之地，终无功利。\";s:6:\"shaoci\";s:72:\"凶：得此爻者，多坎坷，多争诉之事。做官的宜退守。\";}s:9:\"user_data\";a:8:{s:8:\"truename\";s:6:\"杨茂\";s:7:\"shichen\";s:3:\"申\";s:8:\"birthday\";s:17:\"1987年07月26日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 16:56:45\";s:4:\"word\";s:7:\"好 吗\";s:12:\"problem_text\";s:21:\"明天天气好吗？\";s:12:\"problem_type\";s:6:\"杂事\";s:5:\"ce_sn\";s:11:\"00090070001\";}}',1382518605,0),(7,16,77,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:2:\"27\";s:4:\"name\";s:6:\"大过\";s:8:\"fullname\";s:12:\"泽风大过\";s:5:\"thumb\";s:20:\"/data/thumb/s_27.png\";s:5:\"up_id\";s:1:\"2\";s:7:\"down_id\";s:1:\"5\";s:11:\"description\";s:147:\"（卦形：兑上巽下）大过就是过度；大的过度就是暴发的行动，事情到了一定关口，必须果断行动，不要错过。\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:2:\"48\";s:4:\"name\";s:3:\"井\";s:8:\"fullname\";s:9:\"水风井\";s:5:\"thumb\";s:20:\"/data/thumb/s_48.png\";s:5:\"up_id\";s:1:\"6\";s:7:\"down_id\";s:1:\"5\";s:11:\"description\";s:177:\"（卦形：坎上巽下）井是人们生活的必须品，井水永远保持干净，象征君子的美德。但打水的水桶可能倾覆，所以平静中暗藏着凶险。\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:2:\"77\";s:4:\"name\";s:6:\"九四\";s:8:\"position\";s:1:\"4\";s:5:\"yaoci\";s:36:\"九四。栋隆，吉。有它吝。\";s:7:\"xiangci\";s:42:\"象曰：栋隆之吉，不桡乎下也。\";s:8:\"s_gua_id\";s:2:\"27\";s:8:\"b_gua_id\";s:2:\"48\";s:6:\"tiyong\";s:0:\"\";s:12:\"yaoci_jieshi\";s:69:\"九四：屋梁挺直，吉利。但有意外之患则不好应付。\";s:14:\"xiangci_jieshi\";s:90:\"《象辞》说：屋梁挺直之所以吉利，因为屋梁不弯曲则房屋不倾倒。\";s:6:\"shaoci\";s:126:\"吉：得此爻者，多有修造之事，可做大事，勿谋小事。做官的必能受到重用。读书人进取成名。\";}s:9:\"user_data\";a:8:{s:8:\"truename\";s:6:\"杨茂\";s:7:\"shichen\";s:3:\"申\";s:8:\"birthday\";s:17:\"2013年10月23日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 16:58:43\";s:4:\"word\";s:7:\"搞 人\";s:12:\"problem_text\";s:10:\"dfasdfasdf\";s:12:\"problem_type\";s:6:\"杂事\";s:5:\"ce_sn\";s:11:\"00000090002\";}}',1382518723,0),(8,17,127,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:1:\"7\";s:4:\"name\";s:3:\"比\";s:8:\"fullname\";s:9:\"水地比\";s:5:\"thumb\";s:19:\"/data/thumb/s_7.png\";s:5:\"up_id\";s:1:\"6\";s:7:\"down_id\";s:1:\"8\";s:11:\"description\";s:174:\"（卦形：坎上坤下）此卦为比卦；象征大家相亲相爱，和睦吉祥。如果看到别人团结和睦，心里不安才过去团结的人，必然有凶险。\";}s:5:\"b_gua\";a:7:{s:2:\"id\";s:1:\"2\";s:4:\"name\";s:3:\"坤\";s:8:\"fullname\";s:9:\"坤为地\";s:5:\"thumb\";s:19:\"/data/thumb/s_2.png\";s:5:\"up_id\";s:1:\"8\";s:7:\"down_id\";s:1:\"8\";s:11:\"description\";s:300:\"（卦形：坤上坤下）此卦象征大地，大地可以包容万物，好像君子宽阔的胸怀；大地依顺着天资生万物，向前永远奔腾不息。但大地包容既为应当收敛，此时就应该谨行慎言，才不会发生过失，即使不能得到赞赏，也可免除灾祸\";}s:7:\"dongyao\";a:11:{s:2:\"id\";s:3:\"290\";s:4:\"name\";s:6:\"九五\";s:8:\"position\";s:1:\"5\";s:5:\"yaoci\";s:66:\"九五。显比，王用三驱，失前禽。邑人不诫，吉。\";s:7:\"xiangci\";s:99:\"象曰：显比之吉，位正中也。舍逆取顺，失前禽也。邑人不诫，上使中也。\";s:8:\"s_gua_id\";s:1:\"7\";s:8:\"b_gua_id\";s:1:\"2\";s:6:\"tiyong\";s:0:\"\";s:12:\"yaoci_jieshi\";s:174:\"九五：普遍的和洽。君王采用三面包围的方法狩猎，网开一面，有意放走逃奔的野兽。老百姓对君王狩猎毫不惊惧。筮遇此爻吉利。\";s:14:\"xiangci_jieshi\";s:267:\"《象辞》说：普遍的和洽是吉利的，因为九五之爻处于上卦中位，像人守中正之道。放走向前奔逃的，猎取迎面奔窜的，这就是“失前禽”的缘故。老百姓对君王狩猎毫不惊惧，因为君王平时行事端正。\";s:6:\"shaoci\";s:126:\"吉：得此爻者，会先逆后顺，谋事有成，无往不利。做官的会有大的荣耀。读书人会考的佳绩。\";}s:9:\"user_data\";a:8:{s:8:\"truename\";s:9:\"李仲举\";s:7:\"shichen\";s:3:\"申\";s:8:\"birthday\";s:17:\"1987年10月07日\";s:11:\"cesuan_time\";s:26:\"2013年10月23日 16:59:19\";s:4:\"word\";s:7:\"诚 老\";s:12:\"problem_text\";s:117:\"明年打算去成都发展，想测一下，工作是不是顺和，以后发展如何！还有爱情有没有希望\";s:12:\"problem_type\";s:6:\"事业\";s:5:\"ce_sn\";s:11:\"00020020002\";}}',1382518759,0),(9,18,127,'a:4:{s:5:\"s_gua\";a:7:{s:2:\"id\";s:2:\"28\";s:4:\"name\";s:3:\"坎\";s:8:\"fullname\";s:9:\"坎为水\";s:5:\"thumb\";s:20: